import React, { Component } from 'react';
import { connect } from 'react-redux';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import { Card, CardActions, CardHeader, CardText } from 'material-ui/Card';
import Button from '@material-ui/core/Button';

class RoomList extends Component {
    constructor(props){
        super(props);
        this.state = {
            onClicked : false,
        }
    }

    shouldComponentUpdate(){
        if(this.state.onClicked = true) {
            return true;
        } else return false;
    }

    render() {
        var roomlist = this.props.rooms.map(function (room) {
            return (
                <Card>
                    <CardHeader title={"출발지: " + room.dep + " 목적지: " + room.dest} subtitle={"작성자: " + room.writer} actAsExpander={true} showExpandableButton={true} />
                    <CardActions>
                        <Button disabled = {this.state.onClicked} onClick = {function(){
                            this.state.onClicked = true;
                            this.props.onClick('addMyRoom', room.id);
                        }.bind(this)}>
                            참가
                        </Button>
                    </CardActions>
                    <CardText expandable={true}>추가정보</CardText>
                </Card>
            )
        }.bind(this));

        return (
            <MuiThemeProvider>
                {roomlist}
            </MuiThemeProvider>
        );
    }
}

export default connect(
    function (state) {
        return {
            rooms: state.rooms,
        }
    },
    function (dispatch) {
        return {
            onClick: function (type, roomId) {
                dispatch({ type, roomId });
            }
        }
    }
)(RoomList);
